import React, { useState } from "react";
import Components from "./Components";
import Visual from "./Visual";
import "./Creator.css";
import HierarchyComponent from "./HierarchyComponent";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const buttons = [
  { name: "Name1", content: "adsdasjdasdkahdjasjdhakdahd" },
  { name: "Name2", content: "hdfhfghfg" },
  { name: "Name3", content: "cvxcvxcvxcv" },
  { name: "Name4", content: "324234235332" },
  { name: "Name5", content: "oiiewroioweruiow" },
];

export default function Creator() {
  const [hierarchyComponents, setHierarchyComponents] = useState([]);

  const handleAddInHierarchy = (component) => {
    let comp = {
      id: hierarchyComponents.length,
      name: component.name + hierarchyComponents.length,
      content: component.content,
    };
    const updateComps = [...hierarchyComponents, comp];
    setHierarchyComponents(updateComps);
  };

  const handleRemoveFromHierarchy = (name) => {
    const updateComps = [...hierarchyComponents].filter(
      (comp) => comp.name !== name
    );
    setHierarchyComponents(updateComps);
  };

  const handleOnDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(hierarchyComponents);
    const [reorderedItems] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItems);

    setHierarchyComponents(items);
  };

  return (
    <div className="content">
      <div className="components">
        <h1>Components</h1>
        {buttons.map((btn, i) => (
          <Components
            id={i}
            name={btn.name}
            content={btn.content}
            handleAdd={() => handleAddInHierarchy(btn)}
          />
        ))}
      </div>
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <div className="hierarchy">
          <h1>Hierarchy</h1>
          <Droppable droppableId="components">
            {(provided) => {
              <ul {...provided.droppableProps} ref={provided.innerRef}>
                {hierarchyComponents.map((btn, i) => (
                  <Draggable key={btn.name} draggableId={i} index={i}>
                    {(provided) => {
                      return (
                        <HierarchyComponent
                          key={i}
                          id={i}
                          name={btn.name}
                          content={btn.content}
                          handleDelete={() =>
                            handleRemoveFromHierarchy(btn.name)
                          }
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          innerRef={provided.innerRef}
                        />
                      );
                    }}
                    {provided.placeholder}
                  </Draggable>
                ))}
              </ul>;
            }}
          </Droppable>
        </div>
      </DragDropContext>
      <div className="visualisation">
        <h1>Visual</h1>
        {hierarchyComponents.map((btn, i) => (
          <Visual id={i} name={btn.name} content={btn.content} />
        ))}
      </div>
    </div>
  );
}
