import React, { useState } from "react";
import Components from "./Components";
import Visual from "./Visual";
import "./Creator.css";
import HierarchyComponent from "./HierarchyComponent";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

const buttons = [
  { name: "Name1", content: "adsdasjdasdkahdjasjdhakdahd" },
  { name: "Name2", content: "hdfhfghfg" },
  { name: "Name3", content: "cvxcvxcvxcv" },
  { name: "Name4", content: "324234235332" },
  { name: "Name5", content: "oiiewroioweruiow" },
];

const getItems = (count) =>
  Array.from({ length: count }, (v, k) => k).map((k) => ({
    id: `item-${k}`,
    content: `item ${k}`,
  }));

// a little function to help us with reordering the result
const reorder = (list, startIndex, endIndex) => {
  const result = Array.from(list);
  const [removed] = result.splice(startIndex, 1);
  result.splice(endIndex, 0, removed);

  return result;
};

const grid = 8;

const getItemStyle = (isDragging, draggableStyle) => ({
  // some basic styles to make the items look a bit nicer
  userSelect: "none",
  padding: grid * 2,
  margin: `0 0 ${grid}px 0`,

  // change background colour if dragging
  background: isDragging ? "lightgreen" : "grey",

  // styles we need to apply on draggables
  ...draggableStyle,
});

const getListStyle = (isDraggingOver) => ({
  background: isDraggingOver ? "lightblue" : "lightgrey",
  padding: grid,
  width: 250,
});

export default function Creator() {
  const [hierarchyComponents, setHierarchyComponents] = useState([]);

  const handleAddInHierarchy = (component) => {
    let comp = {
      id: hierarchyComponents.length,
      name: component.name + hierarchyComponents.length,
      content: component.content,
    };
    const updateComps = [...hierarchyComponents, comp];
    setHierarchyComponents(updateComps);
  };

  const handleRemoveFromHierarchy = (name) => {
    const updateComps = [...hierarchyComponents].filter(
      (comp) => comp.name !== name
    );
    setHierarchyComponents(updateComps);
  };

  return (
    <div className="content">
      <div className="components">
        <h1>Components</h1>
        {buttons.map((btn, i) => (
          <Components
            id={i}
            name={btn.name}
            content={btn.content}
            handleAdd={() => handleAddInHierarchy(btn)}
          />
        ))}
      </div>
      <div className="hierarchy">
        <h1>Hierarchy</h1>
        {
          /* {hierarchyComponents.map((btn, i) => (
          <HierarchyComponent
            id={i}
            name={btn.name}
            content={btn.content}
            handleDelete={() => handleRemoveFromHierarchy(btn.name)}
          />
        ))} */
          <DragDropContext onDragEnd={this.onDragEnd}>
            <Droppable droppableId="droppable">
              {(provided, snapshot) => (
                <div
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                  style={getListStyle(snapshot.isDraggingOver)}
                >
                  {this.state.items.map((item, index) => (
                    <Draggable
                      key={item.id}
                      draggableId={item.id}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={getItemStyle(
                            snapshot.isDragging,
                            provided.draggableProps.style
                          )}
                        >
                          {item.content}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        }
      </div>
      <div className="visualisation">
        <h1>Visual</h1>
        {hierarchyComponents.map((btn, i) => (
          <Visual id={i} name={btn.name} content={btn.content} />
        ))}
      </div>
    </div>
  );
}
