import React, { useState } from "react";
import "./Components.css";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import ButtonGroup from "react-bootstrap/ButtonGroup";

export default function Components({
  id,
  name,
  content,
  handleAdd,
  handleDelete,
  handleEditContent,
  tags,
}) {
  const [showModal, setShowModal] = useState(false);
  const [newContentText, setNewContentText] = useState("");
  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  return (
    <>
      {showModal && (
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading {tags}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ButtonGroup aria-label="Text Transform">
              <Button variant="secondary">Lowercase</Button>
              <Button variant="secondary">Uppercase</Button>
              <Button variant="secondary">Capitalize</Button>
            </ButtonGroup>
            <ButtonGroup aria-label="Text Alignment">
              <Button variant="secondary">Left</Button>
              <Button variant="secondary">Center</Button>
              <Button variant="secondary">Right</Button>
            </ButtonGroup>
            <InputGroup>
              <InputGroup.Text>Textarea</InputGroup.Text>
              <Form.Control
                as="textarea"
                aria-label="With textarea"
                onChange={(e) => setNewContentText(e.target.value)}
              />
            </InputGroup>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button
              variant="primary"
              onClick={() => handleEditContent(newContentText, id, tags)}
            >
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      )}
      <div className="card">
        <div className="title">
          <h3>
            {id}.{name}
          </h3>
          <p>
            {content} {tags}
          </p>
          <button onClick={handleDelete}>Remove</button>
          <button onClick={handleShow}>Edit</button>
        </div>
      </div>
    </>
  );
}
