import React, { useState } from "react";
import Components from "./Components";
import Visual from "./Visual";
import "./Creator.css";
import HierarchyComponent from "./HierarchyComponent";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

const buttons = [
  { name: "Name1", content: "<b>adsdasjdasdkahdjasjdhakdahd</b>" },
  { name: "Name2", content: "<center><h1>hdfhfghfg</h1></center>" },
  { name: "Name3", content: "<p>Tet paragraph</p>" },
  { name: "Name4", content: "<ul><li>1</li><li>2</li><li>3</li></ul>" },
  { name: "Name5", content: "<h4>H4</h4>" },
];

export default function Creator() {
  const [hierarchyComponents, setHierarchyComponents] = useState([]);

  const handleAddInHierarchy = (component) => {
    let comp = {
      id: hierarchyComponents.length,
      name: component.name + hierarchyComponents.length,
      content: component.content,
    };
    const updateComps = [...hierarchyComponents, comp];
    setHierarchyComponents(updateComps);
  };

  const handleRemoveFromHierarchy = (name) => {
    const updateComps = [...hierarchyComponents].filter(
      (comp) => comp.name !== name
    );
    setHierarchyComponents(updateComps);
  };

  const handleOnDragEnd = (result) => {
    if (!result.destination) {
      return;
    }
    const items = Array.from(hierarchyComponents);
    const [reorderedItems] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItems);

    setHierarchyComponents(items);
  };

  const handleExport = () => {
    const header = `<!DOCTYPE html>
    <html lang="en">
    <head>
        <meta charset="UTF-8">
        <meta http-equiv="X-UA-Compatible" content="IE=edge">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Document</title>
    </head>
    <body>
      `;
    let body = "";
    hierarchyComponents.forEach((element) => {
      body += element;
      body += "\n";
    });
    const footer = `</body>
      </html>``;`;
    const fs = require("fs");
    const data = header + "\n" + boy + "\n" + footer + "\n";
    fs.writeFile("../exports/export.html", data, (err) => {
      if (err) throw err;
    });
  };

  return (
    <div className="content">
      <div className="components">
        <h1>Components</h1>
        {buttons.map((btn, i) => (
          <Components
            id={i}
            name={btn.name}
            content={btn.content}
            handleAdd={() => handleAddInHierarchy(btn)}
          />
        ))}
      </div>
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="droppable">
          {(provided, snapshot) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              //style={getListStyle(snapshot.isDraggingOver)}
            >
              {hierarchyComponents.length !== 0 &&
                hierarchyComponents.map((item, index) => (
                  <Draggable
                    key={item.name}
                    draggableId={item.name}
                    index={index}
                  >
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        // style={getItemStyle(
                        //   snapshot.isDragging,
                        //   provided.draggableProps.style
                        // )}
                      >
                        <HierarchyComponent
                          id={index}
                          name={item.name}
                          content={item.content}
                          handleDelete={() =>
                            handleRemoveFromHierarchy(item.name)
                          }
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
      <div className="visualisation">
        <h1>Visual</h1>
        <button onClick={handleExport}>Export</button>
        {hierarchyComponents.length !== 0 &&
          hierarchyComponents.map((btn, i) => (
            <Visual id={i} name={btn.name} content={btn.content} />
          ))}
      </div>
    </div>
  );
}
