import React, { useState } from "react";
import "./Components.css";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import ButtonGroup from "react-bootstrap/ButtonGroup";

export default function Components({
  id,
  name,
  content,
  handleAdd,
  handleDelete,
  handleEditComponent,
  tags,
  style,
  alignment,
  cap,
  isTable,
  isList,
}) {
  const [showModal, setShowModal] = useState(false);
  const [newContentText, setNewContentText] = useState(content);
  const [newCap, setNewCap] = useState(cap);
  const [newAlign, setNewAlign] = useState(alignment);
  const [newStyle, setNewStyle] = useState("");
  const [colNo, setColNo] = useState(3);
  const [rowNo, setRowNo] = useState(3);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  const addStyle = (style) => {
    let actualStyle = newStyle;
    actualStyle += style;
    setNewStyle(actualStyle);
  };
  const handleTextStyle = (id) => {
    switch (id) {
      case 0:
        addStyle("fw-bold ");
        break;
      case 1:
        setNewStyle("fw-normal ");
        break;
      case 2:
        addStyle("fst-italic ");
        break;
      default:
        break;
    }
  };

  const handleTextAlg = (id) => {
    switch (id) {
      case 0:
        setNewAlign("text-start ");
        break;
      case 1:
        setNewAlign("text-center ");
        break;
      case 2:
        setNewAlign("text-end ");
        break;
      default:
        break;
    }
  };

  const handleTextCap = (id) => {
    switch (id) {
      case 0:
        setNewCap("text-lowercase ");
        break;
      case 1:
        setNewCap("text-uppercase ");
        break;
      case 2:
        setNewCap("text-capitalize ");
        break;
      default:
        break;
    }
  };

  const saveContent = () => {
    handleEditComponent(newContentText, id, tags, newStyle, newCap, newAlign);
    handleClose();
  };

  const setRowNumber = (rows) => {
    setRowNo(rows);
  };

  const setColNumber = (cols) => {
    setColNo(cols);
  };

  return (
    <>
      {showModal && (
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading {tags}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ButtonGroup aria-label="Text Style">
              <Button variant="secondary" onClick={() => handleTextStyle(0)}>
                Bold
              </Button>
              <Button variant="secondary" onClick={() => handleTextStyle(1)}>
                Normal
              </Button>
              <Button variant="secondary" onClick={() => handleTextStyle(2)}>
                Italics
              </Button>
            </ButtonGroup>
            <ButtonGroup aria-label="Text Transform">
              <Button variant="secondary" onClick={() => handleTextCap(0)}>
                Lowercase
              </Button>
              <Button variant="secondary" onClick={() => handleTextCap(1)}>
                Uppercase
              </Button>
              <Button variant="secondary" onClick={() => handleTextCap(2)}>
                Capitalize
              </Button>
            </ButtonGroup>
            <ButtonGroup aria-label="Text Alignment">
              <Button variant="secondary" onClick={() => handleTextAlg(0)}>
                Left
              </Button>
              <Button variant="secondary" onClick={() => handleTextAlg(1)}>
                Center
              </Button>
              <Button variant="secondary" onClick={() => handleTextAlg(2)}>
                Right
              </Button>
            </ButtonGroup>
            {/* {isTable == true && isList == false && (
              <InputGroup>
                <InputGroup.Text>Textarea</InputGroup.Text>
                <Form.Control
                  as="textarea"
                  aria-label="With textarea"
                  onChange={(e) => setNewContentText(e.target.value)}
                  value={newContentText}
                />
              </InputGroup>
            )}
            {isTable == true && isList == false && (
              <InputGroup>
                <InputGroup.Text>Number of Columns</InputGroup.Text>
                <NumericInput
                  min={0}
                  max={100}
                  value={colNo}
                  onChange={(e) => setColNo(e)}
                />
                <InputGroup.Text>Number of Rows</InputGroup.Text>
                <NumericInput
                  min={0}
                  max={100}
                  value={rowNo}
                  onChange={(e) => setRowNo(e)}
                />
              </InputGroup>
            )} */}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={saveContent}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      )}
      <div className="row">
        <div className="col-4 align-self-center">
          <h3>
            {id}.{name}
          </h3>
        </div>
        <div className="col-4 align-self-center">
          <p>
            {content} {tags}
          </p>
        </div>
        <div className="col-4 align-self-center">
          <button
            className="btn btn-sm btn-outline-warning align-self-center"
            onClick={handleDelete}
          >
            Remove
          </button>
        </div>
        <div className="col-4 align-self-center">
          <button
            className="btn btn-sm btn-outline-danger align-self-center"
            onClick={handleShow}
          >
            Edit
          </button>
        </div>
      </div>
    </>
  );
}
