import React, { useState } from "react";
import Components from "./Components";
import Visual from "./Visual";
import "./Creator.css";
import HierarchyComponent from "./HierarchyComponent";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

const buttons = [
  {
    name: "H1",
    content: "Header 1",
    tags: "h1",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
  {
    name: "H2",
    content: "Header 2",
    tags: "h2",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
  {
    name: "H3",
    content: "Header 3",
    tags: "h3",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
  {
    name: "H4",
    content: "Header 4",
    tags: "h4",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
  {
    name: "H5",
    content: "Header 5",
    tags: "h5",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
  {
    name: "P",
    content: "Paragraph",
    tags: "p",
    style: "",
    alignment: "",
    capitalization: "",
    isTable: false,
    isList: false,
  },
];

export default function Creator() {
  const [hierarchyComponents, setHierarchyComponents] = useState([]);

  const handleAddInHierarchy = (component) => {
    let comp = {
      id: hierarchyComponents.length,
      name: component.name + hierarchyComponents.length,
      content: component.content,
      tags: component.tags,
      style: component.style,
    };
    const updateComps = [...hierarchyComponents, comp];
    setHierarchyComponents(updateComps);
  };

  const handleRemoveFromHierarchy = (name) => {
    const updateComps = [...hierarchyComponents].filter(
      (comp) => comp.name !== name
    );
    setHierarchyComponents(updateComps);
  };

  const handleOnDragEnd = (result) => {
    if (!result.destination) {
      return;
    }
    const items = Array.from(hierarchyComponents);
    const [reorderedItems] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItems);

    setHierarchyComponents(items);
  };

  const handleExport = () => {
    const header = `
    <!DOCTYPE html>
    <html lang="en">
    <head>
        <meta charset="UTF-8">
        <meta http-equiv="X-UA-Compatible" content="IE=edge">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Document</title>
    </head>
    <body>
    `;
    let body = "";
    hierarchyComponents.forEach((element) => {
      body += "     ";
      body += element["content"];
      body += "\n";
    });
    const footer = `      </body>
  </html>;`;
    const data = header + "\n" + body + "\n" + footer + "\n";
    console.log(data);
  };

  const getContent = (newContent, tags, style, cap, algn) => {
    if (typeof algn === "undefined") algn = "";
    if (typeof style === "undefined") style = "";
    if (typeof cap === "undefined") cap = "";
    const cont =
      "<" +
      tags +
      " class= " +
      '"' +
      style +
      cap +
      algn +
      '"' +
      ">" +
      newContent +
      "<" +
      "/" +
      tags +
      ">";
    return cont;
  };

  const handleEditContent = (newContent, id, tags, style, cap, algn) => {
    let items = [...hierarchyComponents];
    if (typeof algn === "undefined") algn = "";
    if (typeof style === "undefined") style = "";
    if (typeof cap === "undefined") cap = "";
    items.forEach((element) => {
      if (element.id === id) {
        element.content = getContent(newContent, tags, style, cap, algn);
      }
    });

    setHierarchyComponents(items);
    console.log("clicked");
  };

  return (
    <div className="content">
      <div className="row">
        <div className="components col-md-2">
          <h1>Components</h1>
          {buttons.map((btn, i) => (
            <Components
              id={i}
              name={btn.name}
              content={btn.content}
              handleAdd={() => handleAddInHierarchy(btn)}
            />
          ))}
        </div>
        <div className="hierarchy col-md-2">
          <h1>Hierarchy</h1>
          <DragDropContext onDragEnd={handleOnDragEnd}>
            <Droppable droppableId="droppable">
              {(provided, snapshot) => (
                <div {...provided.droppableProps} ref={provided.innerRef}>
                  {hierarchyComponents.length !== 0 &&
                    hierarchyComponents.map((item, index) => (
                      <Draggable
                        key={item.name}
                        draggableId={item.name}
                        index={index}
                      >
                        {(provided, snapshot) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <HierarchyComponent
                              id={index}
                              name={item.name}
                              content={item.content}
                              tags={item.tags}
                              style={item.style}
                              handleDelete={() =>
                                handleRemoveFromHierarchy(item.name)
                              }
                              handleEditComponent={handleEditContent}
                              cap={item.cap}
                              alignment={item.alignment}
                              isTable={item.isTable}
                              isList={item.isList}
                            />
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </div>
        <div className="visualisation col-md-8">
          <div className="row">
            <div className="col-10">
              <h1>Visual</h1>
            </div>
            <div className="col-2">
              <button
                className="btn btn-info"
                style={(color = "white")}
                onClick={handleExport}
              >
                Export
              </button>
            </div>
          </div>
          {hierarchyComponents.length !== 0 &&
            hierarchyComponents.map((btn, i) => (
              <Visual
                id={i}
                name={btn.name}
                content={getContent(
                  btn.content,
                  btn.tags,
                  btn.style,
                  btn.capitalization,
                  btn.alignment
                )}
                style={btn.style}
                capitalization={btn.capitalization}
                alignment={btn.alignment}
              />
            ))}
        </div>
      </div>
    </div>
  );
}
